`module displayCounter (
    //Inputs
    input clk, rst, 
    input enable, countUp, countDown, pause, 
    //Outputs
    output [6:0] sevenSeg
    );

    //Registers
    reg [3:0] counter;
index
    //Clock Divider
    reg [15:0] clkDiv;
    always @ (posedge clk or posedge rst)
    begin
        if (rst)
            clkDiv <= 16'd0;
        else 
            clkDiv <= clkDiv + 1;
    end

    //Counter
    always @ (posedge rst or posedge clkDiv[15]) 
    begin
if (rst)
            counter <= 0;
        //Enabled Counter
        else if (enable == 1'b1) 
        begin
            //Up 
            if (countUp)
               counter <= counter +1;
            //Down 
            else if (countDown)
                counter <= counter -1;
            //Pause
            else if (pause)
                //Do Nothing 
         end
        else
            //Do Nothing 
    end

    //Display Decoder 
    assign sevenSeg = (counter == 0) ? 7'b1000000 :
    (counter == 1) ? 7'b1111001 :
    (counter == 2) ? 7'b0100100 :
    (counter == 3) ? 7'b0110000 :
    (counter == 4) ? 7'b0011001 :
    (counter == 5) ? 7'b0010010 :
    (counter == 6) ? 7'b0000010 :
    (counter == 7) ? 7'b1111000 :
    (counter == 8) ? 7'b0000000 :
    (counter == 9) ? 7'b0011000 :
    7'b0001000;

endmodule
